from flask import Flask, request, jsonify
from werkzeug.utils import secure_filename
from flask_cors import CORS
import os


app = Flask(__name__)
app.config.from_object(__name__)
CORS(app, resources={r'/*': {'origins': '*'}})

BATTERS = {
    'Mike Trout': {'img': 'data:image/jpeg;base64,', 
            'hr': 35, 'obp': .402, 'slg': .517},
    'Shohei Ohtani': {'img': 'data:image/jpeg;base64,', 
            'hr': 43, 'obp': .427, 'slg': .590}
}

PITCHERS = {
    'Justin Verlander': {'img': 'data:image/jpeg;base64,', 
            'era': 2.78, 'kper9': 10.41, 'bbper9': 2.02 },
    'Max Scherzer': {'img': 'data:image/jpeg;base64,',
            'era': 3.02, 'kper9': 11.07, 'bbper9': 3.40 },
}


@app.route('/upload', methods=['POST'])
def file_upload():
    if 'file' not in request.files:
        return jsonify({'message': 'No file part'}), 400

    file = request.files['file']
    if file.filename == '':
        return jsonify({'message': 'No selected file'}), 400

    if file:
        filename = secure_filename(file.filename)
        file.save(os.path.join('uploads', filename))
        return jsonify({'message': f'File {filename} uploaded successfully'}), 200


@app.route('/ping', methods=['GET'])
def ping_pong():
    return jsonify('pong!')


@app.route('/get_batter', methods=['GET'])
def get_batter():
    name = request.args.get('name')
    response_data = BATTERS[name]
    return jsonify(response_data)


@app.route('/get_pitcher', methods=['GET'])
def get_pitcher():
    name = request.args.get('name')
    response_data = PITCHERS[name]
    return jsonify(response_data)


if __name__ == '__main__':
    os.makedirs('uploads', exist_ok=True)
    app.run(debug=True)
