<template>
    <v-card style="width: 100%; margin: 10px 10px;">
        <!--Player select-->
        <v-row>
            <v-col>
                <v-select
                    :items="names" v-model="name" 
                    @update:modelValue="handlePitcherChange"
                    density="compact" 
                ></v-select>
            </v-col>
        </v-row>
        <v-row style="margin-top: -30px;">
            <!--Player image-->
            <v-col cols="3">
                <v-img fluid
                    :src="data.img"
                ></v-img>
            </v-col>

            <!--Player stats-->
            <v-col cols="9" class="pr-2">
                <div>
                    <table class="stats-table">
                        <thead>
                            <tr>
                            <th>ERA</th>
                            <th>K/9</th>
                            <th>BB/9</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                            <td>{{ data.era }}</td>
                            <td>{{ data.kper9 }}</td>
                            <td>{{ data.bbper9 }}</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </v-col>
        </v-row>
    </v-card>
</template>

<script>
import axios from 'axios';

export default {
    data(){
        return{
            name: 'Select Player',
            defaultData: {img: "data:image/png;base64,",
                   era: '-', kper9: '-', bbper9: '-'},
            data: {},
            names: ['Justin Verlander', 'Max Scherzer'],
        }
    },
    methods: {
        handlePitcherChange() {
            const path = 'http://localhost:5000/get_pitcher';
            axios.get(path, { params: { name: this.name } })
                .then((res) => {
                    console.log(res.data)
                    this.data = res.data;
                })
                .catch((error) => {
                    console.error(error);
                    this.data = this.defaultData;
                });
        },
    },
    created() {
        this.data = this.defaultData;
    },
};
</script>

<style>
.stats-table {
  border-collapse: collapse;
  width: 95%;
  font-size: 12px;
  margin-bottom: 8px;
}

.stats-table th, .stats-table td {
  border: 1px solid #ddd;
  text-align: left;
}

.stats-table th {
  background-color: #f2f2f2;
}
</style>